import { GET_DOGS, SEARCH_DOGS, SET_CURRENT_PAGE} from "./actions";

const initialState = {
  dogs: [],
  currentPage: 1,
  itemsPerPage: 5,
};

const rootReducer = (state = initialState, action) => {
  switch (action.type) {
    default:
      return { ...state };
    case GET_DOGS:
      return { ...state, dogs: action.payload };
    case SEARCH_DOGS:
      return { ...state, dogs: action.payload };
    case SET_CURRENT_PAGE:
      return { ...state, currentPage: action.payload };
  }
};

export default rootReducer;
