import React, { useEffect, useState } from 'react';
import { connect } from 'react-redux';
import Card from '../Card/Card.jsx';
import style from './Cards.module.css';
import { getDogs, searchDogs } from '../../store/actions';
import SearchBar from '../SearchBar/SearchBar.jsx';
function Cards({ dogs, page, pageSize, setPage }) {
  const pageCount = Math.ceil(dogs.length / pageSize);

  const startIndex = page * pageSize;
  const endIndex = startIndex + pageSize;
  const currentDogs = dogs.slice(startIndex, endIndex);

  return (
    <div>
      {/* Renderizar currentDogs */}

      <Pagination page={page} pageCount={pageCount} onPageChange={setPage} />
    </div>
  );
}

function Pagination({ page, pageCount, onPageChange }) {
  const prevPage = page > 0 ? page - 1 : 0;
  const nextPage = page + 1 < pageCount ? page + 1 : page;

  return (
    <div className="pagination">
      <button onClick={() => onPageChange(prevPage)} disabled={page === 0}>
        Prev
      </button>

      {/* Renderizar botones de página */}

      <button onClick={() => onPageChange(nextPage)} disabled={page === pageCount - 1}>
        Next
      </button>
    </div>
  );
}

// Actualizar estado de página
function setPage(page) {
  // actualizar state
}

// MapStateToProps
const mapStateToProps = (state) => ({
  dogs: state.dogs,
  page: state.page,
  pageSize: 10,
});

// MapDispatchToProps
const mapDispatchToProps = (dispatch) => ({
  setPage: setPage,
});

export default connect(mapStateToProps, mapDispatchToProps)(Cards);
