import { useState } from "react";
import * as ReacRouterDom from "react-router-dom";
import { alpha, useTheme } from "@mui/material";
import Box from "@mui/material/Box";
import TextField from "@mui/material/TextField";
import Select from "@mui/material/Select";
import MenuItem from "@mui/material/MenuItem";
import Typography from "@mui/material/Typography";
import Button from "@mui/material/Button";
import Grid from "@mui/material/Grid";
import { useAppDispatch, useAppSelector } from "../hooks/redux-hooks";
import { searchPokemon } from "../store/pokemons/pokemons-actions";

const { useNavigate, useSearchParams } = ReacRouterDom;

export const SearchBox = ({ showSiteName = false }) => {
  const [search] = useSearchParams();
  const navigate = useNavigate();
  const theme = useTheme();
  const types = useAppSelector((state) => state.pokemons.types);
  const dispatch = useAppDispatch();

  const [formData, setFormData] = useState;

  const searchHandler = () => {
    if (formData.q) {
      dispatch(searchPokemon(formData));
      navigate(
        `/search?q=${formData.q}&tipo=${formData.tipo}`
      );
    }
  };

  return (
    <Box
      sx={{
        backgroundColor: alpha(theme.palette.grey[900], 0.7),
        width: "100%",
        display: "flex",
        flexDirection: "column",
        alignItems: "center",
        boxShadow: 3,
        borderRadius: 10,
        mt: 2,
        transition: "all 1s",
        p: 5,
      }}
    >
      {/* Site Name */}
      {showSiteName && (
        <Typography
          component="h1"
          variant="h2"
          align="center"
          color="text.primary"
          display={"inline"}
        >
          <Box color="primary.main" display={"inline"}>
            Pokemons
          </Box>
        </Typography>
      )}

      {/* Search Form */}
      <Box sx={{ mt: 3, width: 1 }}>
        <Grid container spacing={2}>
          <Grid item xs={12} sm={6}>
            <TextField
              id="pokemon-name-ac"
              value={formData.q}
              onChange={(event) => {
                setFormData({ ...formData, q: event.target.value });
              }}
              fullWidth
              placeholder={"Type a pokemon name"}
              variant="outlined"
            />
          </Grid>
          <Grid item xs={12} sm={2}>
            <Select
              id="pokemon-type-select"
              value={formData.type}
              displayEmpty
              fullWidth
              onChange={(event) => {
                setFormData({ ...formData, type: event.target.value });
              }}
              renderValue={(selected) => (selected ? selected : "Type")}
            >
              <MenuItem value="">
                <em>None</em>
              </MenuItem>
              {types.map((type) => (
                <MenuItem key={type.id} value={type.id}>
                  {type.name}
                </MenuItem>
              ))}
            </Select>
          </Grid>

          <Grid item xs={12} sm={2}>
            <Button
              type="submit"
              variant="contained"
              fullWidth
              sx={{ py: 2 }}
              onClick={searchHandler}
            >
              Search
            </Button>
          </Grid>
        </Grid>
      </Box>
    </Box>
  );
};
