import Api from './Api';
import { pathOr } from 'ramda';

const pokemonService = {
  async searchPokemon(searchParams){
    const response = await Api().get('pokemon/name', {
      params: {
        name: searchParams.name,
      },
    });

    return response.data.results.length
      ? response.data.results.map((item) => ({
          id: item.id,
          uuid: item.uuid,
          nombre: item.nombre,
          peso: item.peso,
          altura: item.altura,
          imagen: item.imagen,
          vida: item.vida,
          defensa: item.defensa,
          velocidad: item.velocidad,
          tipos: item.tipos,
          source: item.source,
        }))
      : null;
  },

  async getPokemon(pokemonId) {
    const response = await Api().get(`pokemon/${pokemonId}`);
    const item = response.data;
    return item
      ? {
        id: item.id,
        uuid: item.uuid,
        nombre: item.nombre,
        peso: item.peso,
        altura: item.altura,
        imagen: item.imagen,
        vida: item.vida,
        defensa: item.defensa,
        velocidad: item.velocidad,
        tipos: item.tipos,
        source: item.source,
        }
      : null;
  },
};

export default pokemonService;
