import { GET_POKEMONS, SEARCH_POKEMON } from "./actions";

const initialState = {
  pokemons: [],
};

const rootReducer = (state = initialState, action) => {
  switch (action.type) {
    default:
      return { ...state };
    case GET_POKEMONS:
      return { ...state, pokemons: action.payload };
    case SEARCH_POKEMON:
      return { ...state, pokemons: [action.payload] };
  }
};

export default rootReducer;
