import React from "react";
import * as ReacRouterDom from "react-router-dom";
import Container from "@mui/material/Container";
import Stack from "@mui/material/Stack";
import Slide from "@mui/material/Slide";
import LinearProgress from "@mui/material/LinearProgress";
import { Search, Title } from "./pages";
import { useAppSelector } from "./hooks/redux-hooks";
import { Header } from "./components";

const { HashRouter, Routes, Route, } = ReacRouterDom;

function App() {
  const loading = useAppSelector((state) => state.pokemons.loading);

  return (
    <>
      <Slide direction="down" in={loading} mountOnEnter unmountOnExit>
        <Stack
          sx={{
            position: "fixed",
            top: 0,
            left: 0,
            width: "100%",
            color: "primary",
          }}
          spacing={2}
        >
          <LinearProgress color="primary" />
        </Stack>
      </Slide>
      <HashRouter>
        <Container maxWidth="lg">
          <Header />
          <Routes>
            <Route path="/" element={<Search />} />
            <Route path="/pokemon/:id" element={<Title />} />
          </Routes>
        </Container>
      </HashRouter>
    </>
  );
}

export default App;
