import { useDispatch, useSelector } from "react-redux";
import { useEffect } from "react";
import { fetchPokemonsAction } from "./slices/pokemonsSlice";
import { useState } from "react";

function App() {
  const [pokemonName, setPokemonName] = useState('');
  const pokemons = useSelector(state => state?.pokemons);
  const { loading, pokemonsList, error } = pokemons;

  const dispatch = useDispatch();
  useEffect(() => {
    dispatch(fetchPokemonsAction(pokemonName));
  }, [pokemonName, dispatch]);

  return (
    <>
      <section className="bg-gray-800 min-h-screen">
        <div className="relative container px-4 mx-auto">
          <div className="text-center mb-20">
            <h2 className="mt-10 mb-5 text-5xl font-bold font-heading text-indigo-300">
              Buscador de Pokemons
            </h2>
            <div className="mt-1 flex justify-center">
              <input
                value={pokemonName}
                onChange={e => setPokemonName(e.target?.value)}
                type="text"
                name="email"
                id="email"
                className="shadow-sm text-center focus:ring-indigo-500 p-2 focus:border-indigo-500  sm:text-sm border-gray-300 w-full rounded-md lg:w-1/2"
                placeholder="Search For Pokemon"
              />
            </div>
          </div>
          {loading ? (
            <h1 className="text-green-300 text-3xl text-center">Loading</h1>
          ) : error ? (
            <h1 className="text-red-500 text-lg text-center">{error?.message}</h1>
          ) : (
            <div className="full-width">
              <div className="flex flex-wrap -mx-4 mb-20">
                <div className="w-full">
                  {pokemonsList?.name !== "Error" &&
                    pokemonsList?.map(pokemon => (
                      <div key={pokemon?.uuid}>
                        <div className="py-6 px-8 mb-4 bg-gray-600 rounded-lg">
                          <div className="text-center">
                            <a
                              target="_blank"
                              href={`pokemon/${pokemon?.id}`}
                              className="text-lg  text-indigo-400"
                            >
                              {pokemon?.nombre}
                            </a>
                            <img src={pokemon?.imagen}/>
                          </div>
                        </div>
                      </div>
                    ))}
                </div>
              </div>
            </div>
          )}
        </div>
      </section>
    </>
  );
}

export default App;
