import * as ReacRouterDom from 'react-router-dom';
import Card from '@mui/material/Card';
import CardContent from '@mui/material/CardContent';
import CardMedia from '@mui/material/CardMedia';
import Grid from '@mui/material/Grid';
import Typography from '@mui/material/Typography';
import noPreview from '../img/no-image.png';
const { Link } = ReacRouterDom;

export const PokemonCard = ({ pokemon }) => {
  return (
    <Grid item key={pokemon.id} sm={4} xs={6} md={3}>
      <Link to={`/pokemon/${pokemon.id}`}>
        <Card
          sx={{
            height: '100%',
            display: 'flex',
            flexDirection: 'column',
          }}
        >
          <CardMedia component='img' image={pokemon.imagen ?? noPreview} alt='random' sx={{ height: '250px' }} />
          <CardContent sx={{ flexGrow: 1 }}>
            <Typography gutterBottom variant='h6'>
              {pokemon.nombre}
            </Typography>
          </CardContent>
        </Card>
      </Link>
    </Grid>
  );
};
