import { GET_POKEMONS, SEARCH_POKEMON } from "./actions";

const initialState = {
  dogs: [],
};

const rootReducer = (state = initialState, action) => {
  switch (action.type) {
    default:
      return { ...state };
    case GET_DOGS:
      return { ...state, dogs: action.payload };
    case SEARCH_DOGS:
      return { ...state, dogs: [action.payload] };
  }
};

export default rootReducer;
