import * as ReacRouterDom from 'react-router-dom';
import Grid from '@mui/material/Grid';
import Box from '@mui/material/Box';
import Typography from '@mui/material/Typography';

const { useLocation, Link } = ReacRouterDom;

export const Header = () => {
  const { pathname } = useLocation();
  return (
    <header>
      <Grid container spacing={2}>
        <Grid
          item
          sm={8}
          xs={10}
          pl={2}
          sx={{
            display: 'flex',
            flexDirection: 'column',
            alignItems: 'start',
            justifyContent: 'center',
          }}
        >
          {pathname !== '/' && (
            <Link to='/'>
              <Typography
                component='h1'
                variant='h2'
                sx={{ textAlign: { xs: 'center', sm: 'left' } }}
                color='text.primary'
                gutterBottom
              >
                <Box color='primary.main' display={'inline'}>
                  Pokemons
                </Box>
              </Typography>
            </Link>
          )}
        </Grid>
      </Grid>
    </header>
  );
};
