import { createAsyncThunk, createSlice } from "@reduxjs/toolkit";
import axios from "axios";

export const fetchPokemonsAction = createAsyncThunk(
  "pokemons/list",
  async (user, { rejectWithValue, getState, dispatch }) => {
    try {
      const { data } = await axios.get(`http://localhost:3001/pokemons`);
      return data;
    } catch (error) {
      if (!error?.response) {
        throw error;
      }
      return rejectWithValue(error.response.data);
    }
  }
);

const pokemonsSlices = createSlice({
  name: "pokemons",
  initialState: {},
  extraReducers: builder => {
    builder.addCase(fetchPokemonsAction.pending, (state, action) => {
      state.loading = true;
    });
    builder.addCase(fetchPokemonsAction.fulfilled, (state, action) => {
      state.pokemonsList = action?.payload;
      state.loading = false;
      state.error = undefined;
    });
    builder.addCase(fetchPokemonsAction.rejected, (state, action) => {
      state.loading = false;
      state.error = action.payload;
      state.pokemonsList = undefined;
    });
  },
});

export default pokemonsSlices.reducer;
