import { useEffect } from 'react';
import { useParams } from 'react-router-dom';
import { alpha, useTheme } from '@mui/material';
import Grid from '@mui/material/Grid';
import Container from '@mui/material/Container';
import CssBaseline from '@mui/material/CssBaseline';
import Typography from '@mui/material/Typography';
import FavoriteIcon from '@mui/icons-material/Favorite';
import FavoriteBorderOutlinedIcon from '@mui/icons-material/FavoriteBorderOutlined';
import WatchLaterIcon from '@mui/icons-material/WatchLater';
import WatchLaterIconOutlined from '@mui/icons-material/WatchLaterOutlined';
import { useAppDispatch, useAppSelector } from '../hooks/redux-hooks';
import { PokemonDetailsColumn } from '../components';
import { getPokemon, reset } from '../store/pokemons/pokemons-actions';

export const Pokemon = () => {
  const { id } = useParams();
  const theme = useTheme();
  const pokemon = useAppSelector((state) => state.pokemons.poemon);
  const dispatch = useAppDispatch();

  useEffect(() => {
    dispatch(reset());
    dispatch(getPokemon(id));
  }, [dispatch, id]);

  return (
    pokemon && (
      <main>
        <CssBaseline />
        <Container sx={{ backgroundColor: alpha(theme.palette.grey[900], 0.7), width: '100%', py: 1 }}>
          {/* Pokemon Details */}
          <Grid container direction='row' justifyContent='center' alignItems='center'>
            <Grid item xs={12} md={12} sm={12}>
              <Typography component='h4' variant='h4' align='left' color='text.primary' gutterBottom>
                {pokemon.nombre} {pokemon.tipos && `(${pokemon.tipos})`}{' '}
                {pokemon.peso}-{pokemon.altura}-{pokemon.velocidad}
              </Typography>
            </Grid>
          </Grid>

          <Grid container spacing={2} direction='row' justifyContent='center' alignItems='center'>
            {/* Title Details Column */}
            <Grid item xs={12} sm={12} md={12} lg={6}>
              <TitleDetailsColumn title={title} />
            </Grid>

            {/* Title Trailer Column */}
            {title?.trailer && (
              <Grid item xs={12} sm={12} md={12} lg={6} sx={{ alignSelf: 'start' }}>
                <ImdbPlayer url={title?.trailer}></ImdbPlayer>
              </Grid>
            )}
          </Grid>
        </Container>
      </main>
    )
  );
};
