import Container from '@mui/material/Container';
import Grid from '@mui/material/Grid';
import CardMedia from '@mui/material/CardMedia';
import Typography from '@mui/material/Typography';
import noPreview from '../img/no-image.png';

export const PokemonDetailsColumn = ({ pokemon }) => {
  return (
    pokemon && (
      <>
        <Grid
          container
          spacing={{ xs: 0, sm: 2 }}
          sx={{ ml: 0 }}
          direction='row'
          justifyContent='center'
          alignItems='center'
        >
          <Grid item xs={12} sm={6}>
            <CardMedia
              component='img'
              image={pokemon?.imagen ?? noPreview}
              alt='random'
              sx={{ height: '450px' }}
            />
          </Grid>
          <Grid item xs={12} sm={6}>
            <Container maxWidth='sm' sx={{ opacity: 0.8, pt: { xs: 2 } }}>
              {pokemon?.tipos && (
                <Typography component='p' variant='subtitle2' align='left' color='text.primary' paragraph>
                  Tipos: {pokemon?.tipos}
                </Typography>
              )}

              {pokemon?.altura && (
                <Typography component='p' variant='subtitle2' align='left' color='text.primary' paragraph>
                  Altura: {pokemon?.altura}
                </Typography>
              )}
            </Container>
          </Grid>
        </Grid>
      </>
    )
  );
};
