import React, { useState, useEffect } from 'react';
import { connect } from 'react-redux';
import { useParams, useLocation } from 'react-router-dom';
import style from './AddDog.module.css';
import { getDogs } from '../../store/actions';
import { useNavigate } from 'react-router-dom';

function AddDog(props) {
  const [dog, setDog] = useState(false);
  const { id } = useParams();
  const query = useQuery();
  const source = query.get('source');
  const navigate = useNavigate(); // Obtener la funci贸n de navegaci贸n

  useEffect(() => {
    props.getDogs();
    props.dogs.forEach((p) => {
      if (p.id === Number(id) && p.source === source) {
        setDog(p);
      }
    });
  }, [id, source]);

  const handleHomeClick = () => {
    // Redirigir a la ruta "/"
    navigate('/');
  };

  return <div className={style.container}></div>;
}

function useQuery() {
  const { search } = useLocation();

  return React.useMemo(() => new URLSearchParams(search), [search]);
}

const mapDispatchToProps = (dispatch) => {
  return {
    getDogs: () => {
      dispatch(getDogs());
    },
  };
};

const mapStateToProps = (state) => {
  return {
    dogs: state.dogs,
  };
};

export default connect(mapStateToProps, mapDispatchToProps)(AddDog);
